import React, { Component } from 'react'
import { connect } from 'react-redux'
import {
    getQuestionsAction, receiveQuestions
} from '../actions/home'
import { getAllUsersAction, receiveUsers } from '../actions/login'
import Question from './Question'


class Home extends Component {
    componentDidMount() {
        //get the user id from the adress link
        console.log(window.location.href);
        const fulUrl = window.location.href;
        const lastSlash = fulUrl.lastIndexOf("/");
        console.log(lastSlash);
        const id = fulUrl.substr((lastSlash + 1));
        console.log("id in home", id);
        //or simply
        console.log(this.props.match.params.id);
        //get all users data
        //  this.props.getAllUsersAction();
        //get specific user data using id 

        this.props.getAllUsersAction()
        console.log("users in home total", this.props.users);
        this.props.getQuestionsAction();
        console.log("users in home total", this.props.questions);
        this.unansweredQs(this.props.questions, this.props.users.answers);

    }
    unansweredQs(questions, answeredquestions) {
        console.log("this is geting answered questions");
        for (let i in questions) {
            for (let j in answeredquestions)
                if (i == j) {
                    console.log("this question is answered");

                }
        }
    }
    render() {
        const Id = this.props.match.params.id
        console.log("my try id", Id);
        console.log("data in home total", this.props.users[Id]);
        let user = this.props.users[Id];
        console.log("my user", user);
        return (
            <div>
                <p>welcome{user.name} </p>
                <button>answered</button>

                <ul>
                    {Object.keys(user.answers).map((answeredQ) => (
                        <li key={answeredQ}>
                            <Question id={answeredQ} />
                        </li>
                    ))}
                </ul>

                <button>unanswered</button>

            </div>
        )

    }

}
const mapStateToProps = (state) => {
    console.log("state to home component", state.home);
    return {
        users: state.login.users,
        questions: state.home.questions,
        //   images: Object.keys(state.login.users.avatarURL)
    }
}

const mapDispatchToProps = {
    getQuestionsAction,
    receiveQuestions,
    getAllUsersAction,
    receiveUsers
}
export default connect(mapStateToProps, mapDispatchToProps)(Home)
