import { combineReducers } from 'redux';
import { RECEIVE_USERS } from '../actions/login'

const initialState = {
    users: []

};
function login(state = initialState, action) {
    console.log("welcome to reducer function");
    switch (action.type) {
        case RECEIVE_USERS:
            console.log("action user in reducer".action.user)
            return {
                ...state,
                users: state.users,
            }
        default:
            return state
    }

}
export const reducers = combineReducers({
    login,
});
