import { RECEIVE_USERS, SAVEUSER } from '../actions/login'

const initialState = {};

export const login = (state = initialState, action) => {
    //  console.log("welcome to reducer function");
    switch (action.type) {
        case RECEIVE_USERS:
            //        console.log("action user in reducer", action.users)
            return {
                ...state,
                users: action.users,
            }
        case SAVEUSER:
            return {
                ...state,
                loggeduser: action.user,
            }


        default:
            //      console.log("default");
            return state


    }

}
