import React, { Component } from 'react'
import { connect } from 'react-redux'

class QusetionDetails extends Component {
    render() {
        console.log("coming props to question details", this.props);
        return (
            <div>
                <p>question details </p>
                {(this.props.questiondetails !== undefined) && (
                    <ul>
                        <li>would you rather</li>
                        <li>{this.props.questiondetails.optionOne.text}</li>
                        {/*<li>{this.props.questiondetails.optionOne.votes.length}</li>*/}
                        <li>{this.props.questiondetails.optionTwo.text}</li>
                        {/*  <li>{this.props.questiondetails.optionTwo.votes.length}</li>*/}
                        <li>your vote {this.props.yourVote}</li>
                        <li>no of people vote for option 1 {this.props.votesforop1.length}</li>
                        <li>no of people vote for option 2 {this.props.votesforop2.length}</li>
                    </ul>

                )}
            </div>
        )


    }
}
const mapStateToProps = (state) => {
    console.log("state to login component", state.login);
    return {
        yourVote: state.vote.votes,
        questiondetails: state.question.questiondata,
        //   images: Object.keys(state.login.users.avatarURL)
    }
}


export default connect(mapStateToProps)(QusetionDetails)
