import { combineReducers } from 'redux';
import { RECEIVE_USERS } from '../actions/login'

function login(state = {}, action) {
    switch (action.type) {
        case RECEIVE_USERS:
            return {
                ...state,
                ...action.users
            }
        default:
            return state
    }
}
export const reducers = combineReducers({
    login,
});
