import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom';
import NavBar from './NavBar';
import { saveQuestionAction } from '../actions/newquestion'

class NewQ extends Component {
    state = {
        optionone: " ",
        optiontwo: " "
    }
    handleChange1 = (event) => {
        console.log("change", event);
        this.setState({ optionone: event.target.value })
    }
    handleChange2 = (event) => {

        this.setState({ optiontwo: event.target.value })
    }

    handleSubmit = (event) => {
        event.preventDefault();

        this.props.saveQuestionAction(this.state.optionone, this.state.optiontwo, this.props.loggeduser);

    }
    render() {
        return (
            <div>
                <NavBar />
                <p>new question</p>
                <div>
                    <p>would you rather </p>
                    <form onSubmit={this.handleSubmit}>
                        <label>
                            first option :
                            <input
                                type="text"
                                placeholder="optionOne"
                                onChange={this.handleChange1}

                            />
                        </label>
                        <label>
                            second option :
                            <input
                                type="text"
                                placeholder="optionTwo"
                                onChange={this.handleChange2}

                            />
                        </label>
                        <button>submit</button>
                    </form>
                </div>
            </div>
        )
    }
}
const mapStateToProps = (state) => {


    return {

        loggeduser: state.login.loggeduser,
        //   images: Object.keys(state.login.users.avatarURL)
    }
}

const mapDispatchToProps = {
    saveQuestionAction,
}
export default connect(mapStateToProps, mapDispatchToProps)(NewQ)
