/*
import { createStore } from 'redux'
import { VOTES, OP1VOTES, OP2VOTES, UPDATEQUESTIONSTATE } from '../actions/votes'
import { store } from './store'

const currentState = store.getState();

export const vote = (state = currentState, action) => {
    switch (action.type) {
        case VOTES:
            return {
                ...state,
                userVote: action.answer,
            }
        case OP1VOTES:
            return {
                ...state,
                Op1votes: action.Op1votes,
            }
        case OP2VOTES:
            return {
                ...state,
                Op2votes: action.Op2votes,
            }

        default:
            return state
    }
}
*/
