import { VOTES, OP1VOTES, OP2VOTES, UPDATEQUESTIONSTATE } from '../actions/votes'

const initialState = {};

export const vote = (state = initialState, action) => {
    switch (action.type) {
        case VOTES:
            return {
                ...state,
                userVote: action.answer,
            }
        case OP1VOTES:
            return {
                ...state,
                Op1votes: action.Op1votes,
            }
        case OP2VOTES:
            return {
                ...state,
                Op2votes: action.Op2votes,
            }
        case UPDATEQUESTIONSTATE:
            return {
                ...state,
                [action.qid]: {
                    ...state[action.qid],
                    optionOne:
                        action.answer === "optionOne" ? {
                            ...state[action.qid].optionOne.votes.concat(authedUser)
                        } :
                            state[action.qid].optionOne,
                    optionTwo:
                        action.answer === "optionTwo" ? {
                            ...state[action.qid].optionTwo.votes.concat(authedUser)
                        } :
                            state[action.qid].optionTwo
                },
            }


        default:
            return state
    }
}
