import React, { Component } from 'react'
import { Link } from 'react-router-dom';

class UnAnsQuestion extends Component {
    state = {
        showdetails: false,
    }
    formSubmit = (event) => {
        event.preventDefault();
        console.log("recording your answer");
        this.setState({ showdetails: true })

    }
    onValueChange(event) {
        console.log("you choose");
        console.log(event.target.value);

    }
    render() {
        console.log("inside question", this.props.obj);

        return (
            <div>
                <p>would you rather</p>
                <form onSubmit={this.formSubmit}>
                    <div className="radio">
                        <label>
                            <input
                                type="radio"
                                value="optionOne"
                                onChange={this.onValueChange}
                            />
                            {this.props.obj.optionOne.text}
                        </label>
                    </div>
                    <div className="radio">
                        <label>
                            <input
                                type="radio"
                                value="optionTwo"
                                onChange={this.onValueChange}
                            />
                            {this.props.obj.optionTwo.text}
                        </label>
                    </div>
                    <button className="btn btn-default" type="submit">
                        Submit
                    </button>
                    {this.state.showdetails === true && (<Link to='/questions' >view</Link>)}
                </form>

            </div>
        )
    }
}

export default UnAnsQuestion
