import { VOTES, OP1VOTES, OP2VOTES, UPDATEQUESTIONSTATE } from '../actions/votes'

const initialState = {};

export const vote = (state = initialState, action) => {
    switch (action.type) {
        case VOTES:
            return {
                ...state,
                userVote: action.answer,
            }
        case OP1VOTES:
            return {
                ...state,
                Op1votes: action.Op1votes,
            }
        case OP2VOTES:
            return {
                ...state,
                Op2votes: action.Op2votes,
            }
        case UPDATEQUESTIONSTATE:
            console.log("the state inside the reducer", state);
            return {
                ...state.home,
                questions: {
                    ...state.home.questions[action.qid],
                    optionOne: action.answer === "optionOne" ? {
                        ...state.home.questions[action.qid].optionOne.votes.concat(action.authedUser)
                    } : state.home.questions[action.qid].optionOne,
                    optionTwo: action.answer === "optionTwo" ? {
                        ...state.home.questions[action.qid].optionTwo.votes.concat(action.authedUser)
                    } : state.home.questions[action.qid].optionTwo
                }
            }
        default:
            return state
    }
}
