import { _saveQuestionAnswer } from '../_DATA.js'
export const VOTES = 'VOTES'
export const OP1VOTES = 'OP1VOTES'
export const OP2VOTES = 'OP2VOTES'
export const UPDATEQUESTIONSTATE = 'UPDATEQUESTIONSTATE'

export const receiveVotesAction = (authedUser, qid, answer) => {
    console.log("inputs to receiveVotesAction", authedUser, qid, answer);
    return {
        type: VOTES,
        authedUser,
        qid,
        answer,
    }

}
export const updateAnswerdQuestionsAction = (question, userVote) => {
    return {
        type: UPDATEQUESTIONSTATE,
        question,
        userVote,
    }
}
export const receiveOp1VotesAction = (Op1votes) => {
    // console.log("recever user action", users);
    return {
        type: OP1VOTES,
        Op1votes
    }

}
export const receiveOp2VotesAction = (Op2votes) => {
    // console.log("recever user action", users);
    return {
        type: OP2VOTES,
        Op2votes
    }

}

export const saveuserAnswerAction = (authedUser, qid, ans) => (dispatch) => {
    console.log("before dispatching the receiveVotesAction");
    dispatch(receiveVotesAction(authedUser, qid, ans))
    // let answer = { [qid]: ans }
    _saveQuestionAnswer({
        authedUser,
        qid,
        answer: ans,
    }
    ).then((res) => {
        console.log("response from save answer", res);
    })
}
