import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom';
import NavBar from './NavBar';


class Leader extends Component {
    render() {

        return (
            <div>
                <NavBar />
            </div>
        )
    }


}
const mapStateToProps = (state) => {
    console.log("state to login component", state.login);
    if (state.login !== undefined) {
        let questionsScore = [];
        let answersScore = [];
        let newstate = state.login.users.map((user) => {
            questionsScore.push(user.questions.length);
            state.login.users[user].questionsScore = user.questions.length;
            state.login.users[user].answersScore = user.answers.length
            answersScore.push(user.answers.length);
        })
        console.log("the state after modification", newstate);
    }
    return {
        users: state.login.users,

        //   images: Object.keys(state.login.users.avatarURL)
    }
}
export default connect(mapStateToProps)(Leader)
