import { VOTES, OP1VOTES, OP2VOTES } from '../actions/votes'

const initialState = {};

export const vote = (state = initialState, action) => {
    switch (action.type) {
        case VOTES:
            return {
                ...state,
                votes: action.votes,
            }
        case OP1VOTES:
            return {
                ...state,
                Op1votes: action.Op1votes,
            }
        case OP2VOTES:
            return {
                ...state,
                Op2votes: action.Op2votes,
            }



        default:
            return state
    }
}
