import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom';
import NavBar from './NavBar';

class NewQ extends Component {
    state = {
        optionone: " ",
        optiontwo: " "
    }
    handleChange1 = (event) => {
        console.log("change", event);
        this.setState({ optionone: event.target.value })
    }
    handleChange2 = (event) => {
        console.log("change", event);
        this.setState({ optiontwo: event.target.value })
    }

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.optionone);
        console.log(this.state.optiontwo);
    }
    render() {
        return (
            <div>
                <NavBar />
                <p>new question</p>
                <div>
                    <p>would you rather </p>
                    <form onSubmit={this.handleSubmit}>
                        <input
                            type="text"
                            placeholder="optionOne"
                            onChange={this.handleChange1}

                        />
                        <input
                            type="text"
                            placeholder="optionTwo"
                            onChange={this.handleChange}

                        />
                        <button>submit</button>
                    </form>
                </div>
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    //  console.log("i want to see the state content", state);
    console.log("data to home", state.home);
    // console.log("data from user", state.login);

    return {
        users: state.login.users,
        questions: state.home.questions,
        loggeduser: state.login.loggeduser,
        //   images: Object.keys(state.login.users.avatarURL)
    }
}

const mapDispatchToProps = {

}
export default connect(mapStateToProps, mapDispatchToProps)(NewQ)
