import React, { Component } from 'react'
import { connect } from 'react-redux'
import { getQuestionsAction, receiveQuestions } from '../actions/home'

class AnsQuestion extends Component {
    viewData = (vote, voteop1, voteop2) => {
        console.log("show counts");
        console.log("your vote", vote);
        console.log("who choosed op1", voteop1);
        console.log("who choosed op2", voteop2);
    }
    render() {
        // console.log("initial data", this.props.questions);
        console.log("obj", this.props.obj);
        console.log("vote", this.props.vote);
        //what should be shown would you rather ,option one ,option two , auther ,viewpul button

        return (
            <ul>
                <li>answered question</li>
                <li> auther{this.props.obj.author}</li>
                <li>would you rather</li>
                <li>{this.props.obj.optionOne.text}   {(this.props.vote === 'optionOne') && 'your vote'}</li>
                <li>{this.props.obj.optionTwo.text}   {(this.props.vote === 'optionTwo') && 'your vote'}</li>
                <li>
                    <button onClick={() => this.viewData(this.props.vote, this.props.obj.optionOne.votes, this.props.obj.optionTwo.votes)}>view pull</button>
                </li>


            </ul>
        )
    }
}
const mapStateToProps = (state) => {

    //  console.log("data to home", state.home);

    return {

        questions: state.home.questions,
        //   images: Object.keys(state.login.users.avatarURL)
    }
}
const mapDispatchToProps = {
    getQuestionsAction,
    receiveQuestions,

}
export default connect(mapStateToProps, mapDispatchToProps)(AnsQuestion)
