import React, { Component } from 'react'
import { connect } from 'react-redux'
import { getAllUsersAction, receiveUsers } from '../actions/login'

class Login extends Component {
    componentDidMount() {
        console.log("component did mount");
        this.props.getAllUsersAction()


    }
    render() {
        console.log("props", this.props);
        if (this.props.users !== undefined) {
            let { users } = this.props.users;
        }
        return (
            <div>

                <h3 className='center'>welcome</h3>
                <ul className='dashboard-list'>
                    {(this.props.users !== undefined) && (
                        this.props.users.map((user) => (
                            <li key={user}>
                                {user}
                            </li>
                        ))
                    )
                    }
                </ul>

            </div>
        )
    }
}
const mapStateToProps = (state) => {
    console.log("state to login component", state);
    if (state.login.users !== undefined) {
        return {
            users: Object.keys(state.login.users)
        }
    }
}
const mapDispatchToProps = {
    getAllUsersAction,
    receiveUsers
}
export default connect(mapStateToProps, mapDispatchToProps)(Login)
