import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom';
import { getAllUsersAction, receiveUsers } from '../actions/login'

class Login extends Component {
    state = {
        id: ' '

    }
    componentDidMount() {
        console.log("component did mount");
        this.props.getAllUsersAction()


    }
    getUserId(id) {
        console.log("user id");
        //   this.setState({ id: id });
    }
    render() {
        console.log("props", this.props);
        if (this.props.users !== undefined) {
            let { users } = this.props.users;
        }
        return (
            <div>

                <h3 className='center'>welcome</h3>
                <ul className='dashboard-list'>
                    {(this.props.users !== undefined) && (
                        Object.keys(this.props.users).map((user, i) => (
                            <li key={i}>
                                <button onClick={this.getUserId()}>{this.props.users[user].name}</button>
                                <img
                                    src={this.props.users[user].avatarURL}

                                    className='avatar'
                                />

                            </li>
                        ))
                    )

                    }
                </ul>

            </div>
        )
    }
}
// <Link to={`/Home/${this.props.users[user].id}`} >{this.props.users[user].name}</Link>

const mapStateToProps = (state) => {
    console.log("state to login component", state.login);
    return {
        users: state.login.users,
        //   images: Object.keys(state.login.users.avatarURL)
    }
}

const mapDispatchToProps = {
    getAllUsersAction,
    receiveUsers
}
export default connect(mapStateToProps, mapDispatchToProps)(Login)
